import React, { Fragment } from "react"

import styled from "styled-components"
import { Canvas } from "react-three-fiber"

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: #333;
`

const Application = () => {
  return (
    <Container>
      <Canvas>
        <mesh>
          <boxBufferGeometry 
            attach="geometry"
            args={[1, 1, 1]}
          />
          <meshStandardMaterial
            attach="material"
          />
        </mesh>
      </Canvas>
    </Container>
  )
}

export default Application
